/*
 * 6. Составить программу: определения наибольшего из двух чисел а и b.
 */

public class Task06 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		// TODO Auto-generated method stub
		int a = 5;
		int b = 5;
		if (a > b) {
		System.out.println("больше число a");
		} else if (b > a) {
		System.out.println("больше число b");
		} else if (a == b) {
		System.out.println("числа равны");
		} else {
			System.out.println("Houston, we've had a problem");
		}

	}

}
